import sys
# To fix the communication system,
# add a subroutine to detect a start-of-packet marker in the datastream
# In the protocol being used by the Elves,
# the start of a packet is indicated by
# a sequence of four characters that are all different.

# example 1 should == 7
# datastream = 'mjqjpqmgbljsphdztnvjfqwrcgsmlb'

# example should == 5
# datastream = 'bvwbjplbgvbhsrlpgdmjqwftvncz'

# # example should == 6
# datastream = 'nppdvjthqldpwncqszvftbrmjlhg'

# # example should == 10
# datastream = 'nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg'

# # example should == 11
# datastream = 'zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw'

datastream = ''

for i in range(len(datastream)):
    x = set(datastream[i:i+4])
    if len(x) == 4:
        print(i+4)
        sys.exit("The sequence stopped")
